// src/ClockInOut.js
import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './ClockInOut.css';

const ClockInOut = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editDate, setEditDate] = useState(null);
  const [editClockIn, setEditClockIn] = useState(new Date());
  const [editClockOut, setEditClockOut] = useState(new Date());

  // Fetch current user (including clockEntries) from server
  const fetchUser = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No token found');
        return;
      }
      const response = await axios.get('/api/users/current-user', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUser(response.data);
      setError('');
    } catch (err) {
      console.error(err);
      setError('Failed to fetch user status.');
    }
  }, []);

  useEffect(() => {
    fetchUser();
  }, [fetchUser]);

  // Handler for clock in using the server endpoint
  const handleClockIn = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(
        '/api/users/current-user/clock-in',
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      // Update user state with returned user data
      setUser(response.data.user || response.data);
    } catch (err) {
      console.error(err);
      setError('Failed to clock in.');
    }
  };

  // Handler for clock out using the server endpoint
  const handleClockOut = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(
        '/api/users/current-user/clock-out',
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser(response.data.user || response.data);
    } catch (err) {
      console.error(err);
      setError('Failed to clock out.');
    }
  };

  // Group clock entries by day using the timestamp field
  const groupEntriesByDay = () => {
    if (!user || !user.clockEntries) return {};
    const grouped = {};
    user.clockEntries.forEach(entry => {
      // Always convert the timestamp to a Date object
      const entryDate = new Date(entry.timestamp);
      const dateKey = entryDate.toLocaleDateString();
      if (!grouped[dateKey]) {
        grouped[dateKey] = [];
      }
      grouped[dateKey].push(entry);
    });
    return grouped;
  };

  // Open the edit modal for a specific day
  const handleEdit = (day) => {
    setEditDate(day);
    const grouped = groupEntriesByDay();
    const dayEntries = grouped[day.toLocaleDateString()] || [];
    const clockInEntry = dayEntries.find(entry => entry.type === 'clockIn');
    const clockOutEntry = dayEntries.find(entry => entry.type === 'clockOut');
    setEditClockIn(clockInEntry ? new Date(clockInEntry.timestamp) : day);
    setEditClockOut(clockOutEntry ? new Date(clockOutEntry.timestamp) : day);
    setShowModal(true);
  };

  // Save the edited entries by sending them to the server
  const handleSave = async (e) => {
    e.preventDefault();
    if (!user || !editDate) return;
    try {
      const token = localStorage.getItem('token');
      // Prepare payload with the target day and new times (ISO strings)
      const payload = {
        date: editDate.toISOString().split('T')[0], // Only date part
        clockIn: editClockIn ? editClockIn.toISOString() : undefined,
        clockOut: editClockOut ? editClockOut.toISOString() : undefined,
      };
      const response = await axios.put(
        '/api/users/current-user/time-entries',
        payload,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      // Update user state with updated data from the server
      setUser(response.data.user || response.data);
      setShowModal(false);
    } catch (err) {
      console.error('Error updating time entries:', err);
      setError('Failed to update time entries.');
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  // Updated formatDate function: always parse the input to a Date.
  const formatDate = (dateInput) => {
    const date = new Date(dateInput);
    if (isNaN(date.getTime())) return 'Invalid date';
    return new Intl.DateTimeFormat('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      second: 'numeric',
      timeZoneName: 'short',
    }).format(date);
  };

  return (
    <div className="clock-in-out-container">
      <h1 className="status">{user && user.clockedIn ? 'Clocked In' : 'Clocked Out'}</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div className="button-container">
        <button className="clock-button" onClick={handleClockIn} disabled={user && user.clockedIn}>
          Clock In
        </button>
        <button className="clock-button" onClick={handleClockOut} disabled={user && !user.clockedIn}>
          Clock Out
        </button>
      </div>
      <div className="entries-grid">
        {Object.entries(groupEntriesByDay()).map(([date, entries]) => (
          <div className="day-entries" key={date}>
            <h3>{date}</h3>
            <div className="entry-item">
              <div className="entry-type">Clock In:</div>
              <div className="entry-time">{formatDate(entries.find(entry => entry.type === 'clockIn')?.timestamp)}</div>
            </div>
            <div className="entry-item">
              <div className="entry-type">Clock Out:</div>
              <div className="entry-time">{formatDate(entries.find(entry => entry.type === 'clockOut')?.timestamp)}</div>
            </div>
            <button className="edit-button" onClick={() => handleEdit(new Date(date))}>
              <span role="img" aria-label="edit">✏️</span>
            </button>
          </div>
        ))}
      </div>
      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Edit Time Entries for {editDate.toLocaleDateString()}</h2>
            <form onSubmit={handleSave}>
              <div>
                <label>Clock In:</label>
                <DatePicker
                  selected={editClockIn}
                  onChange={(date) => setEditClockIn(date)}
                  showTimeSelect
                  timeIntervals={15}
                  timeCaption="Time"
                  dateFormat="MMMM d, yyyy h:mm aa"
                  name="clockIn"
                />
              </div>
              <div>
                <label>Clock Out:</label>
                <DatePicker
                  selected={editClockOut}
                  onChange={(date) => setEditClockOut(date)}
                  showTimeSelect
                  timeIntervals={15}
                  timeCaption="Time"
                  dateFormat="MMMM d, yyyy h:mm aa"
                  name="clockOut"
                />
              </div>
              <div className="modal-buttons">
                <button type="submit">Save Changes</button>
                <button type="button" onClick={handleCloseModal}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ClockInOut;
